nta.declaration:
// Place global declarations here.
const int N = 12;
const int M = 16;
const int P = 30;
const int K = 2;
const int MAX_T = 10;
const int queue_column = N-1; // line where robots queue for the delivery point
const int wait_column = 0;    // line where robots without a task rests
const int pick_column = N-4; // line where robots travel to reach the desidered pods line in order to pick a pod
const int delivery_column = N-3; // line where robots travel to reach the desidered pods line in order to deliver a pod
const int new_task_column = wait_column + 1;

int freePods = P;
int head=0;
int tail=0;
int queue_length=0;

typedef struct {
    bool isRobotOn;
} Square;

Square matrix[M][N]; 

typedef struct{
    int x;
    int y;
} Position;

Position delivery = {11,0};
Position entry = {9,15};

typedef struct {
    int y;
    int x;
    bool assigned;
} Pod;

Pod pods[P];

typedef struct{
    Pod pod;
    int id;
} Task;

Task tasks[MAX_T];

urgent chan pick, release, claim;

void initAll(){

    int i, x, y;

    for(i=0, x=2, y=1; i<P; i++){
        pods[i].assigned=false;
        pods[i].x=x;
        pods[i].y=y;
        x++;
        if(x==8){
            x=2;
            y=y+3;
        }

    }

}

template.declaration:
clock x;

int fint_test(double x) {
    int y=0;
    while(y<x) 
        y++;
    return y;

}

Task create_task(){
    double rand;
    int i;
    bool flag=false;
    Task t;
    do {
        rand=random(P-1);
        i= fint_test(rand); 
        if(pods[i].assigned==false){
            flag=true;
            pods[i].assigned=true;
            //pods[i].x = 2+i; pods[i].y = 4+i; //TOREMOVE
        }
    } while(!flag);
    t.pod=pods[i];
    t.id = i;
    freePods--;
    //i++; //TOREMOVE
    return t;
}

void dequeue() {
    queue_length--;
    head=(head+1)%MAX_T;
}

void enqueue(){
    queue_length++;
    tasks[tail]=create_task();
    tail=(tail+1)%MAX_T;
}

// Place local declarations here.

clock t;

bool arrived_pod, arrived_delivery, arrived_back, repositioned;
int x = 9; 
int y = 15;
Task myTask;

void claimTask() {
    myTask = tasks[head];
    arrived_pod = false;
    arrived_delivery = false;
    arrived_back = false;
    repositioned=false;
}

void freePod() {
    pods[myTask.id].assigned = false;
    freePods++;
}

bool isFree(int x, int y) {

    return !matrix[y][x].isRobotOn;

}

void move(int h, int v){
    matrix[y][x].isRobotOn=false;
    x+=h;
    y+=v;
    matrix[y][x].isRobotOn=true;
}


void move_up(){
    if(y>0 && isFree(x,y-1)){
        move(0,-1);
    }
}


void move_down(){
    if(y<M-1 && isFree(x,y+1)){
        move(0,1);
    }
}



void move_left(){
    if(x>0 && isFree(x-1,y)){
        move(-1,0);
    }
}



void move_right(){
    if(x< N-1 && isFree(x+1,y)){
        move(1,0);    
    }
}


void next_step_to_pod() {

    if (x == pick_column && y > myTask.pod.y) {    //robot is in pick row and needs to reach the pod's line
        move_up();         
    } else if (y == myTask.pod.y && x >= myTask.pod.x) { //robot is in pod's line
            if (x != myTask.pod.x) { //not reached pod yet
                move_left();
            }
    } else if (y == myTask.pod.y && x < myTask.pod.x && x > new_task_column) {
            move_left();
    } else if (x == new_task_column && y != M-1) {
            move_down();
    } else if (x == wait_column) {
            move_right();
    } else if (y == M-1) {
        if (x < pick_column) {
            move_right();
        } else if (x > pick_column) {
            move_left();
        }
    }

    if (x == myTask.pod.x && y == myTask.pod.y) {
        arrived_pod = true;
    }
}


void next_step_to_delivery() {

    if( x==myTask.pod.x && y==myTask.pod.y ){ //robot is taking the Pod
        move_down();
    } else if( x==queue_column ){  //robot is on the delivery column
        move_up();
    } else if( x<queue_column-1 ){
        move_right();         
    } else if( x==queue_column-1 && isFree(queue_column,y)){
        move_right();
    } else {
        move_down();
    }


    if(x==delivery.x && y==delivery.y ){
        arrived_delivery=true;
        return;
    }   
}

void next_step_to_return() {
    //da delivery, spostamento a sx per raggiungere la colonna N-3
    if( y==0 && x!=delivery_column){
        move_left();
    } else if( x==delivery_column && y!=myTask.pod.y - 1){ //da delivery row , spostamento in basso per raggiungere la riga pod +1 
        move_down();
    } else if(y==myTask.pod.y-1 && x!=myTask.pod.x ){ //da riga pod + 1  , spostamento a sx per raggiungere la colonna pod 
        move_left();
    } else if(y==myTask.pod.y-1 && x==myTask.pod.x ){ //da riga pod + 1  e colonna del pod, spostamento in basso per consegnare il pod
        if(isFree(x,y+1)){
            move_down();
            arrived_back = true;
        }
    }
}

void next_step_to_reposition(){
    if(x!=1){
        move_left();
    } else if(x==1 && isFree(x-1,y)){
            move_left();
            repositioned=true;
    } else if(y!=M-1 && isFree(x,y+1)){
          move_down();
        }

}

nta.system:
// Place template instantiations here.
robots(int[0,1] i) = Robot();
human = Human(5);
dispatcher = TaskDispatcher(1000); // non soddisfa la prorpietà, cioè non va in overflow
